package sergio;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;//Ejercicio niko8//Realiza el metodo espar que devuelva si el numero recibido es par o no.//Autor: Sergio Tobal//Fecha: 16-01-2012public class niko8 {	public static boolean espar(int numero) {		boolean c;		c=false;		if (numero%2==0) {			c=true;		}		return (c);	}		public static void main(String[] args) throws Exception, IOException {		BufferedReader lectura = new BufferedReader(new InputStreamReader(System.in));		int n;		boolean r;		System.out.println("Introduce numero:");		n=Integer.parseInt(lectura.readLine());		r=espar(n);		if (r==true) {			System.out.println(n+"Es par");		}else{			System.out.println(n+"Es impar");		}	}}